  `title` int(22) NOT NULL DEFAULT '0' COMMENT '排序',
  PRIMARY KEY (`id`),
  KEY `newscats` (`pid`,`isstate`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='广告位图片表';CREATE TABLE `kw_businesses` (
  `user_id` int(10) unsigned NOT NULL,
  `business_name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `business_introduction` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `contact` varchar(15) COLLATE utf8_unicode_ci DEFAULT NULL,
