  `local_phone` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `location` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `rate_count` int(11) DEFAULT NULL,
  PRIMARY KEY (`user_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;INSERT INTO `kw_businesses` VALUES ('1', '联想集团有限公司', '联想集团公司成立于1984年，由中科院计算所投资20万元人民币和11名科技人员创办，
                    现已发展成为一家在信息产业内多元化发展的大型企业集团，富有创新性的国际化的科技公司，由联想及原IBM个人电脑事业部所组成。', '胡锐', '0000-00-00', '', '安徽  合肥', '');INSERT INTO `kw_businesses` VALUES ('3', 'qweqwe', '', 'guw', '0000-00-00', '', '', '');INSERT INTO `kw_businesses` VALUES ('4', '北极光', '', '北极光', '0000-00-00', '', '', '');CREATE TABLE `kw_cart` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `zpid` int(11) NOT NULL,
  `user_token` int(11) NOT NULL COMMENT '用户token',
  `num` int(11) NOT NULL,
  `sendtime` varchar(50) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COMMENT='购物车表';CREATE TABLE `kw_certificate` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '新闻表',
